const account = Account.fromWebAuthnP256(credential)

const client = createWalletClient({
  chain: tempoModerato,
  transport: http(),
})

// Use with Viem APIs
const hash = await client.sendTransactionSync({
  account,
  data: '0xcafebabe',
  to: '0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef',
})

