import {
  blobsToCommitments,
  toBlobs,
  blobsToProofs,
  toBlobSidecars,
  stringToHex
} from 'viem'

const blobs = toBlobs({ data: stringToHex('hello world') })
const commitments = blobsToCommitments({ blobs, kzg })
const proofs = blobsToProofs({ blobs, commitments, kzg })

const sidecars = toBlobSidecars({ blobs, commitments, proofs })
