describe('return value: sign', () => {
  test('default', async () => {
    const account = await toCoinbaseSmartAccount({
      client,
      owners: [owner],
      nonce: 70n,
    })

    await writeContract(client, {
      ...account.factory,
      functionName: 'createAccount',
      args: [[pad(owner.address)], 70n],
    })
    await mine(client, {
      blocks: 1,
    })

    const signature = await account.sign({
      hash: '0xd9eba16ed0ecae432b71fe008c98cc872bb4cc214d3220a36f365326cf807d68',
    })

    const result = await verifyHash(client, {
      address: await account.getAddress(),
      hash: '0xd9eba16ed0ecae432b71fe008c98cc872bb4cc214d3220a36f365326cf807d68',
      signature,
    })

    expect(result).toBeTruthy()
  })

  test('counterfactual', async () => {
    const account = await toCoinbaseSmartAccount({
      client,
      owners: [owner],
      nonce: 141241n,
    })

    const signature = await account.sign({
      hash: '0xd9eba16ed0ecae432b71fe008c98cc872bb4cc214d3220a36f365326cf807d68',
    })

    const result = await verifyHash(client, {
      address: await account.getAddress(),
      hash: '0xd9eba16ed0ecae432b71fe008c98cc872bb4cc214d3220a36f365326cf807d68',
      signature,
    })

    expect(result).toBeTruthy()
  })

  test('behavior: owner uses `sign` instead of `signTypedData`', async () => {
    const owner = privateKeyToAccount(accounts[0].privateKey)
    // @ts-expect-error
    owner.signTypedData = undefined

    const account = await toCoinbaseSmartAccount({
      client,
      owners: [owner],
      nonce: 70n,
    })

    await writeContract(client, {
      ...account.factory,
      functionName: 'createAccount',
      args: [[pad(owner.address)], 70n],
    })
    await mine(client, {
      blocks: 1,
    })

    const signature = await account.sign({
      hash: '0xd9eba16ed0ecae432b71fe008c98cc872bb4cc214d3220a36f365326cf807d68',
    })

    const result = await verifyHash(client, {
      address: await account.getAddress(),
      hash: '0xd9eba16ed0ecae432b71fe008c98cc872bb4cc214d3220a36f365326cf807d68',
      signature,
    })

    expect(result).toBeTruthy()
  })
})

